package day2;

public class Demo4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 字母与数字的混合运算
		System.out.println('a' - 1);
		System.out.println('a' + 1);
		System.out.println('a' * 2);
		System.out.println('a' / 2);
		
		// 把数字转换成字母
		// 下面输出为 b
		System.out.println((char)('a' + 1));
	}
	
}
