package day2;

public class Demo7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 关系运算符的输出结果必然为 boolean 类型
		
		System.out.println(1 == 2);
		System.out.println(1 != 2);
		System.out.println(1 < 2);
		System.out.println(1 > 2);
		System.out.println(1 <= 2);
		System.out.println(1 >= 2);
		
		/* 输出结果：
		 * false
			true
			true
			false
			true
			false
		 */
	}

}
