package day3;

public class Demo8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("4 << 1 = " + (4 << 1 ));
		System.out.println("4 << 2 = " + (4 << 2 ));
		System.out.println("32 >> 1 = " + (32 >> 1));
		System.out.println("4 >> 2 = " + (4 >> 2 ));
		System.out.println("4 >>> 2 = " +(4 >>> 2));
		System.out.println("-4 >>> 2 = " +(-4 >>> 2));
		
		System.out.println(("-4 的二进制数: ") + Integer.toBinaryString(-4));
	}

}
